#include <stdio.h>

char *logo[] = {
	"...................",
	"..*.....*****......",
	".**....*******.....",
	".**....**...**.....",
	".**....**..........",
	".**....**...**.....",
	".**...****.*****...",
	".**..*****.****....",
	".**....**...**.....",
	".**....**...**..**.",
	".****..**...******.",
	"..***..**....****..",
	"...................",
};

int main() {
	int x, y;

	for(y = 0; y < 13; y++) {
		for(x = 0; x < 19; x++) {
			printf("\t\t.byt\t$%x\n", (logo[y][x] == '*')? 0x10 : 0x00);
		}
	}
	return 0;
}
